mapper.select:
select
  id, addr_num, name, is_delet, create_time, update_time
from addr_shop


        and id = #{param.id}


        and addr_num = #{param.addrNum}


        and name = #{param.name}


        and is_delet = #{param.isDelet}


        and create_time = #{param.createTime}


        and update_time = #{param.updateTime}


limit #{offset}, #{limit}

select count(1)
from addr_shop


        and id = #{id}


        and addr_num = #{addrNum}


        and name = #{name}


        and is_delet = #{isDelet}


        and create_time = #{createTime}


        and update_time = #{updateTime}
