mapper.select:
select
  id, date_id, template_name, is_using, id_delete, create_time
from calendar_template


        and id = #{param.id}


        and date_id = #{param.dateId}


        and template_name = #{param.templateName}


        and is_using = #{param.isUsing}


        and id_delete = #{param.idDelete}


        and create_time = #{param.createTime}


limit #{offset}, #{limit}

select count(1)
from calendar_template


        and id = #{id}


        and date_id = #{dateId}


        and template_name = #{templateName}


        and is_using = #{isUsing}


        and id_delete = #{idDelete}


        and create_time = #{createTime}
