mapper.select:
select
  id, shop_id, code, note, value
from config_b_rider


        and id = #{id}


        and shop_id = #{shopId}


        and code = #{code}


        and note = #{note}


        and value = #{value}

select count(1)
from config_b_rider


        and id = #{id}


        and shop_id = #{shopId}


        and code = #{code}


        and note = #{note}


        and value = #{value}
