mapper.select:
select
  id, date_id, employee_id, order_complete_num, order_complete_price, invite_user_num, invite_user_price, positive_comment_bonus, negative_comment_forfeit, negative_comment_num, positive_comment_num, overtime_num, overtime_price, other_bonus, other_forfeit, is_delete, create_time, create_by, update_time, update_by
from employee_salary_dayrecord


        and id = #{param.id}


        and date_id = #{param.dateId}


        and employee_id = #{param.employeeId}


        and order_complete_num = #{param.orderCompleteNum}


        and order_complete_price = #{param.orderCompletePrice}


        and invite_user_num = #{param.inviteUserNum}


        and invite_user_price = #{param.inviteUserPrice}


        and positive_comment_bonus = #{param.positiveCommentBonus}


        and negative_comment_forfeit = #{param.negativeCommentForfeit}


        and negative_comment_num = #{param.negativeCommentNum}


        and positive_comment_num = #{param.positiveCommentNum}


        and overtime_num = #{param.overtimeNum}


        and overtime_price = #{param.overtimePrice}


        and other_bonus = #{param.otherBonus}


        and other_forfeit = #{param.otherForfeit}


        and is_delete = #{param.isDelete}


        and create_time = #{param.createTime}


        and create_by = #{param.createBy}


        and update_time = #{param.updateTime}


        and update_by = #{param.updateBy}


limit #{offset}, #{limit}

select count(1)
from employee_salary_dayrecord


        and id = #{id}


        and date_id = #{dateId}


        and employee_id = #{employeeId}


        and order_complete_num = #{orderCompleteNum}


        and order_complete_price = #{orderCompletePrice}


        and invite_user_num = #{inviteUserNum}


        and invite_user_price = #{inviteUserPrice}


        and positive_comment_bonus = #{positiveCommentBonus}


        and negative_comment_forfeit = #{negativeCommentForfeit}


        and negative_comment_num = #{negativeCommentNum}


        and positive_comment_num = #{positiveCommentNum}


        and overtime_num = #{overtimeNum}


        and overtime_price = #{overtimePrice}


        and other_bonus = #{otherBonus}


        and other_forfeit = #{otherForfeit}


        and is_delete = #{isDelete}


        and create_time = #{createTime}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and update_by = #{updateBy}
