mapper.select:
select
  id, employee_id, type, record_explain, num, record_time, status, month_id
from employee_salary_record


        and id = #{param.id}


        and employee_id = #{param.employeeId}


        and type = #{param.type}


        and record_explain = #{param.desc}


        and num = #{param.num}


        and record_time = #{param.recordTime}


        and status = #{param.status}


        and month_id = #{param.monthId}


order by record_time desc
limit #{offset}, #{limit}

select count(1)
from employee_salary_record


        and id = #{id}


        and employee_id = #{employeeId}


        and type = #{type}


        and record_explain = #{desc}


        and num = #{num}


        and record_time = #{recordTime}


        and status = #{status}


        and month_id = #{monthId}
