mapper.select:
select
  id, message_id, message_type, title, content, push_time, order_code, employee_id, status, is_delete, create_time, create_by, update_time, update_by
from message


        and id = #{param.id}


        and message_id = #{param.messageId}


        and message_type = #{param.messageType}


        and title = #{param.title}


        and content = #{param.content}


        and push_time = #{param.pushTime}


        and order_code = #{param.orderCode}


        and employee_id = #{param.employeeId}


        and status = #{param.status}


        and is_delete = #{param.isDelete}


        and create_time = #{param.createTime}


        and create_by = #{param.createBy}


        and update_time = #{param.updateTime}


        and update_by = #{param.updateBy}


limit #{offset}, #{limit}

select count(1)
from message


        and id = #{id}


        and message_id = #{messageId}


        and message_type = #{messageType}


        and title = #{title}


        and content = #{content}


        and push_time = #{pushTime}


        and order_code = #{orderCode}


        and employee_id = #{employeeId}


        and status = #{status}


        and is_delete = #{isDelete}


        and create_time = #{createTime}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and update_by = #{updateBy}
