mapper.select:
 select  e.employee_id,e.name,e.phone_num,e.sex,e.is_delete, e.front_id_card, e.behind_id_card,e.employee_type,e.is_delete,
        r.location_id,r.regist_time, r.check_time, r.hr, r.invitation_code,r.is_online,
        e.holding_id_card, e.id_card,e.avatar,
        s.check_content,
        t.tname,
        sl.name as salary_level
 from (rider r,t_apartment t, employee e)
     left join separation_application s on  s.employee_id=e.employee_id
     left join  salary_level sl on sl.id=e.sarlary_level

     r.employee_id = e.employee_id  and t.id = r.location_id and r.rider_type != 0

         and r.employee_id = #{rider.employeeId}


         and e.employee_type = #{rider.riderType}


         and r.location_id = #{rider.locationId}


         and r.regist_time >= #{rider.registTime}


         and r.regist_time <=  #{rider.checkTime}


         and r.hr = #{rider.hr}


         and r.invitation_code = #{rider.invitationCode}



         and e.is_delete = #{rider.isDelete}


          and e.is_delete = 1 or e.is_delete = 2


         and r.is_online = #{rider.isOnline}


         and e.name = #{rider.name}


         and e.phone_num = #{rider.phoneNum}


         and e.sex = #{rider.sex}


order by r.check_time desc
     limit #{offset},#{limit}

select   count(distinct r.employee_id)
from rider r left join employee e on r.employee_id = e.employee_id

     r.rider_type != 0

        and r.employee_id = #{rider.employeeId}


        and e.employee_type = #{rider.riderType}


        and r.location_id = #{rider.locationId}


        and r.regist_time >= #{rider.registTime}


        and r.regist_time <=  #{rider.checkTime}


        and r.hr = #{rider.hr}


        and r.invitation_code = #{rider.invitationCode}


        and e.is_delete = #{rider.isDelete}


        and e.is_delete = 1 or e.is_delete = 2


        and r.is_online = #{rider.isOnline}


        and e.name = #{rider.name}


        and e.phone_num = #{rider.phoneNum}


        and e.sex = #{rider.sex}

select e.employee_id, e.name,e.phone_num,e.sex,e.is_delete, e.front_id_card, e.behind_id_card, e.holding_id_card, e.id_card,e.avatar,e.employee_type,
       r.location_id, r.hr, r.invitation_code,
       i.apply_time,i.check_state,i.update_time,i.check_content,
       t.tname
from rider r ,employee e ,in_application i,t_apartment t

    e.employee_id = r.employee_id and r.employee_id=i.employee_id and t.id = r.location_id

        and r.employee_id = #{rider.employeeId}


        and e.employee_type = #{rider.riderType}


        and r.location_id = #{rider.locationId}


        and i.apply_time >= #{rider.registTime}


        and i.apply_time <=  #{rider.checkTime}


        and r.hr = #{rider.hr}


        and r.invitation_code = #{rider.invitationCode}


        and e.is_delete = #{rider.isDelete}


        and e.name = #{rider.name}


        and e.phone_num = #{rider.phoneNum}


        and e.sex = #{rider.sex}


order by i.apply_time desc
limit #{offset},#{limit}

select count(1)
from rider r ,employee e ,in_application i

    r.employee_id = e.employee_id and r.employee_id=i.employee_id

        and r.employee_id = #{rider.employeeId}


        and e.employee_type = #{rider.riderType}


        and r.location_id = #{rider.locationId}


        and i.apply_time >= #{rider.registTime}


        and i.apply_time <=  #{rider.checkTime}


        and r.hr = #{rider.hr}


        and r.invitation_code = #{rider.invitationCode}


        and e.is_delete = #{rider.isDelete}


        and e.name = #{rider.name}


        and e.phone_num = #{rider.phoneNum}


        and e.sex = #{rider.sex}

select r.employee_id, r.location_id,r.hr, r.invitation_code,
       e.name,e.phone_num,e.sex,e.is_delete, e.front_id_card, e.behind_id_card, e.holding_id_card, e.id_card,e.avatar,e.employee_type,
       s.apply_time ,s.check_state,s.update_time,s.check_content,
       t.tname
from rider r ,employee e ,separation_application s,t_apartment t

    r.employee_id = e.employee_id and r.employee_id=s.employee_id and t.id = r.location_id

        and r.employee_id = #{rider.employeeId}


        and e.employee_type = #{rider.riderType}


        and r.location_id = #{rider.locationId}


        and s.apply_time >= #{rider.registTime}


        and s.apply_time <=  #{rider.checkTime}


        and r.hr = #{rider.hr}


        and r.invitation_code = #{rider.invitationCode}


        and e.is_delete = #{rider.isDelete}


        and e.name = #{rider.name}


        and e.phone_num = #{rider.phoneNum}


        and e.sex = #{rider.sex}


order by s.apply_time desc
limit #{offset},#{limit}

