mapper.select:
select  count(distinct e.employee_id)
from employee e,rider r,t_apartment t,rider_schedule s

    e.employee_id = r.employee_id
    and e.employee_id = s.employee_id
    and t.id = r.location_id
    and e.is_delete = 0

        and e.employee_id = #{rider.employeeId}


        and e.name = #{rider.name}


        and e.phone_num = #{rider.phoneNum}


        and r.location_id=#{rider.locationId}


        and r.rider_type=#{rider.riderType}

select  distinct  e.name,e.employee_id,e.phone_num,t.tname,#{rider.startTime} as start_time,#{rider.endTime} as end_time
from employee e,rider r,t_apartment t

    e.employee_id = r.employee_id
    and t.id = r.location_id
    and e.is_delete = 0

        and e.employee_id = #{rider.employeeId}


        and e.name = #{rider.name}


        and e.phone_num = #{rider.phoneNum}


        and r.location_id=#{rider.locationId}


        and r.rider_type=#{rider.riderType}


limit #{offset},#{limit}

select sum(s.work_end_time-s.work_start_time)
from rider_schedule s
where s.employee_id=#{id}
  and s.work_start_time IS NOT NULL
  and s.work_start_time between #{start} and #{end}
  and s.work_end_time between #{start} and #{end}

select count(1) from distribute_order
where employee_id=#{id}
and order_finish_time between #{start} and #{end}

select count(1) from distribute_order
where employee_id=#{id}
  and comment_level >= 3
  and order_finish_time between #{start} and #{end}

select count(1) from distribute_order
where employee_id=#{id}
  and comment_level <  3
  and order_finish_time between #{start} and #{end}

select count(1) from reject_record
where
    employee_id=#{id}
    and record_type = 2
    and record_time between #{start} and #{end}

select
    e.name,e.employee_id,t.tname,e.phone_num,e.employee_type,
       r.is_online
from employee e,rider r,t_apartment t


    e.is_delete=0 and r.rider_type!=0 and e.employee_id=r.employee_id and r.location_id=t.id

        and e.name = #{rider.name}


        and r.employee_id = #{rider.employeeId}


        and r.employee_id = #{rider.employeeId}


        and e.employee_type = #{rider.riderType}


        and r.is_online = #{rider.isOnline}


        and r.location_id=#{rider.locationId}


        and e.phone_num = #{rider.phoneNum}


limit #{offset},#{limit}

select  count(1)
from employee e,rider r,t_apartment t

    e.is_delete=0 and r.rider_type!=0 and e.employee_id=r.employee_id and r.location_id=t.id

        and e.name = #{rider.name}


        and r.employee_id = #{rider.employeeId}


        and r.is_online = #{rider.isOnline}


        and e.employee_type = #{rider.riderType}


        and r.location_id=#{rider.locationId}


        and e.phone_num = #{rider.phoneNum}

select
  id, schedule_id, worktime_id, employee_id, work_start_time, work_end_time, create_time, create_by, update_time, update_by
from rider_schedule


        and id = #{id}


        and schedule_id = #{scheduleId}


        and worktime_id = #{worktimeId}


        and employee_id = #{employeeId}


        and work_start_time = #{workStartTime}


        and work_end_time = #{workEndTime}


        and create_time = #{createTime}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and update_by = #{updateBy}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from rider_schedule


        and id = #{id}


        and schedule_id = #{scheduleId}


        and worktime_id = #{worktimeId}


        and employee_id = #{employeeId}


        and work_start_time = #{workStartTime}


        and work_end_time = #{workEndTime}


        and create_time = #{createTime}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and update_by = #{updateBy}
