mapper.select:
select
  id, shop_id, shop_name, shop_description, shop_status, is_delete, create_time, create_by, update_time, update_by
from shop_info


        and shop_id = #{s.shopId}


        and shop_name = #{s.shopName}


        and shop_status = #{s.shopStatus}


        and is_delete = #{s.isDelete}


limit #{offset},#{limit}

select count(1)
from shop_info


        and id = #{id}


        and shop_id = #{shopId}


        and shop_name = #{shopName}


        and shop_description = #{shopDescription}


        and shop_status = #{shopStatus}


        and is_delete = #{isDelete}


        and create_time = #{createTime}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and update_by = #{updateBy}
