mapper.select:
select
  id, order_code, status, create_time, detail
from f50m_p_bus.t_order_record


        and id = #{param.id}


        and order_code = #{param.orderCode}


        and status = #{param.status}


        and create_time = #{param.createTime}


        and detail = #{param.detail}


order by create_time desc
limit #{offset}, #{limit}

select count(1)
from f50m_p_bus.t_order_record


        and id = #{id}


        and order_code = #{orderCode}


        and status = #{status}


        and create_time = #{createTime}


        and detail = #{detail}
