mapper.select:
select
  id, version_name, path, update_time, is_online, version_id
from version_con


        and id = #{param.id}


        and version_name = #{param.versionName}


        and path = #{param.path}


        and update_time = #{param.updateTime}


        and is_online = #{param.isOnline}


        and version_id = #{param.versionId}


limit #{offset}, #{limit}

select count(1)
from version_con


        and id = #{id}


        and version_name = #{versionName}


        and path = #{path}


        and update_time = #{updateTime}


        and is_online = #{isOnline}


        and version_id = #{versionId}
